for sitemap_url in sitemap_url_list:
    response = requests.get(sitemap_url)

    if response.status_code == 200:
        soup = BeautifulSoup(response.text, "html.parser")

        table_header_row = soup.select_one(
            ".sold-result-table table tr.research-table-header"
        )

        if table_header_row:
            column_headers = [
                header.text.strip() for header in table_header_row.find_all("th")
            ]

            print("Column Headers:")
            print(column_headers)

            data_rows = soup.select(
                ".sold-result-table table tr:not(.research-table-header)"
            )

            for row in data_rows:
                row_data = [data.text.strip() for data in row.find_all("td")]
                print("Row Data:")
                print(row_data)
